# include <stdio.h>

int main()
{
    int hours;
    float total_salary, tax, net_income;

    scanf("%d", &hours);

    // 计算总工资
    if(hours <= 40)
        total_salary = hours * 1000;
    else
        total_salary = 40 * 1000 + (hours - 40) * 1500;

    // 计算税金
    if(total_salary <= 300)
        tax = total_salary * 0.15;
    else if(total_salary <= 450)
        tax = 300 * 0.15 + (total_salary - 300) * 0.20;
    else
        tax = 300 * 0.15 + 150 * 0.20 + (total_salary - 450) * 0.25;

    // 计算净收入
    net_income = total_salary - tax;

    printf("工资总额：%.0f\n", total_salary);
    printf("税金：%.0f\n", tax);
    printf("净收入：%.0f\n", net_income);

    return 0;
}
