solution:
#include <stdio.h>

int main() {
    int num = 42;
    float pi = 3.14;
    char letter = 'A';

    printf("整型变量: %d\n", num);
    printf("浮点型变量: %.2f\n", pi);
    printf("字符型变量: %c\n", letter);
    return 0;
}
