int main() {
    int sum = 15 + 25;
    int result1 = sum * 2;
    int result2 = 50 / 3;
    float result3 = 50.0 / 3;
    printf("%d\n", result1);
    printf("%d\n", result2);
    printf("%f\n", result3);
    return 0;
}
