solution:
#include <stdio.h>

int main() {
    int a = 5;
    float b = 2.5;

    int sum_int = a + b;
    float sum_float = a + b;

    printf("整型结果：%d\n", sum_int);
    printf("浮点型结果：%f\n", sum_float);
    return 0;
}
