solution:
#include <stdio.h>

int main() {
    int num, count = 0;
    for (int i = 0; i < 10; i++) {
        scanf("%d", &num);
        if (num % 2 == 0) {
            count++;
        }
    }
    printf("%d\n", count);
    return 0;
}
