solution:
#include <stdio.h>

int main() {
    int arr[3][4];
    for(int i = 0; i < 3; i++) {
        for(int j = 0; j < 4; j++) {
            scanf("%d", &arr[i][j]);
        }
    }
    for(int i = 0; i < 3; i++) {
        int sum = 0;
        for(int j = 0; j < 4; j++) {
            sum += arr[i][j];
        }
        printf("%d\n", sum);
    }
    return 0;
}
