#include <stdio.h>

int main() {
    // 定义并初始化数组
    int arr[5] = {1, 2, 3, 4, 5};
    // 定义指向数组的指针
    int *p = arr;

    // 使用数组下标方式输出
    printf("数组下标方式：");
    for (int i = 0; i < 5; i++) {
        printf("%d ", arr[i]);
    }
    printf("\n");

    // 使用指针方式输出
    printf("指针方式：");
    for (int i = 0; i < 5; i++) {
        printf("%d ", *(p + i));  // 也可以写成 p[i]
    }
    printf("\n");

    return 0;
}
